using System.Collections; using System.Collections.Generic; using UnityEngine;  public class ControllerScript : MonoBehaviour {      int current_camera = 0;     int current_image = 0;      public GameObject CamerHolder;     public bool Toggle = false; 	// Use this for initialization 	void Start () { 		 	}      // Update is called once per frame     void Update()     {                  if (OVRInput.GetDown(OVRInput.Button.PrimaryIndexTrigger))         {             Toggle = !Toggle;         }          if (OVRInput.GetDown(OVRInput.Button.PrimaryTouchpad))         {             Vector2 Pos = OVRInput.Get(OVRInput.Axis2D.PrimaryTouchpad);              if (Pos.x > 0.7 && Mathf.Abs(Pos.y) < 0.3)      // Right                 Go_Right();             if (Pos.x < -0.7 && Mathf.Abs(Pos.y) < 0.3)     // Left                 Go_Left();             if (Pos.y > 0.7 && Mathf.Abs(Pos.x) < 0.3)     // Forward                 Go_Forward();             if (Pos.y < -0.7 && Mathf.Abs(Pos.x) < 0.3)     // Backward                 Go_Backward();            }               }      void Go_Left()     {          if (current_camera < 2 && current_image == 0)         {             Toggle = false;             ++current_camera;             CamerHolder.transform.Translate(+250f, 0f, 0f,Space.World);         }      }      void Go_Right()     {          if (current_camera > 0 && current_image == 0)         {             Toggle = false;             --current_camera;             CamerHolder.transform.Translate(-250f, 0f, 0f,Space.World);         }     }      void Go_Forward ()     {         if (current_image == 0)         {             Toggle = false;             current_image++;             CamerHolder.transform.position = new Vector3(250f, -37f, 400f);         }          else  if (current_image == 1)         {             Toggle = false;             current_image++;             CamerHolder.transform.position = new Vector3(250f, 0f, 800f);         }     }      void Go_Backward()     {         if (current_image == 1)         {             Toggle = false;             current_image--;             CamerHolder.transform.position = new Vector3(0f, -37f, 22.22f);         }          else if (current_image == 2)         {             Toggle = false;             current_image--;             CamerHolder.transform.position = new Vector3(250f, -37f, 400f);         }     } } 